#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

backend=".."

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

apply()
{
    if [ $host = "macOS" ]; then

        sed -i "" $1 $2
    else
        sed -i $1 $2
    fi
}

#--------------------------------------------------------------------------------------------------

getOs()
{
    case `uname` in
    Darwin*) echo "macOS";;
    *)       echo "other";;
    esac
}

#--------------------------------------------------------------------------------------------------
# Configuration
#--------------------------------------------------------------------------------------------------

host=$(getOs)

cp "$backend"/index.vbml "$backend"/indexLite.vbml

apply 's/index.vbml/indexLite.vbml/' "$backend"/indexLite.vbml
apply '/bittorrent/d'                "$backend"/indexLite.vbml
